import React from "react";
import homeBg from '../assets/home_bg.png';

function Home() {
  return (
    <section
      className="mt-[-100px]relative bg-cover bg-center min-h-screen flex items-center justify-center"
      style={{ backgroundImage: `url(${homeBg})` }}
    >
      {/* Overlay for better text readability */}
      <div className="absolute inset-0 bg-black/50"></div>

      {/* Content */}
      <div className="relative text-center px-4">
        {/* Heading - Montserrat */}
        <h1 className="font-heading text-5xl md:text-6xl lg:text-7xl text-white mb-4">
          HEY THERE! I'M VISHAL
        </h1>

        {/* Subheading / Body - Lato */}
        <p className="font-sans text-white text-lg md:text-2xl mb-6">
          A Passionate Software Developer Crafting Modern Web Solutions
        </p>

        {/* Tech stack snippet */}
        <p className="font-sans text-gray-300 text-sm md:text-base mb-8">
          React | Node.js | Tailwind CSS | JavaScript
        </p>

        {/* Call-to-action button */}
        <button className="bg-cyan-500 hover:bg-cyan-600 text-white py-3 px-8 rounded-full shadow-lg cursor-pointer transition transform hover:scale-105 font-sans">
          Whatsapp me
        </button>
      </div>
    </section>
  );
}

export default Home;
