import { useState, useEffect, useRef } from "react";

const slidesData = [
  {
    image: "https://via.placeholder.com/600x400",
    title: "Our Service 1",
    description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit.",
    buttonText: "Learn More",
  },
  {
    image: "https://via.placeholder.com/600x400",
    title: "Our Service 2",
    description: "Suspendisse potenti. Sed dignissim leo vel nisl.",
    buttonText: "Learn More",
  },
  {
    image: "https://via.placeholder.com/600x400",
    title: "Our Service 3",
    description: "Cras malesuada arcu nec enim. Nulla vulputate magna.",
    buttonText: "Learn More",
  },
];

export default function Slider() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const slideInterval = useRef();

  // Go to next slide
  const nextSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === slidesData.length - 1 ? 0 : prevIndex + 1
    );
  };

  // Go to previous slide
  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? slidesData.length - 1 : prevIndex - 1
    );
  };

  // Autoplay functionality
  useEffect(() => {
    slideInterval.current = setInterval(nextSlide, 3000); // Change slide every 3 seconds
    return () => clearInterval(slideInterval.current);
  }, []);

  return (
    <div className="relative w-full max-w-4xl mx-auto overflow-hidden">
      <div
        className="flex transition-transform duration-700"
        style={{ transform: `translateX(-${currentIndex * 100}%)` }}
      >
        {slidesData.map((slide, index) => (
          <div
            key={index}
            className="w-full flex-shrink-0 px-4 py-8 bg-gray-200"
          >
            <img
              src={slide.image}
              alt={slide.title}
              className="w-full h-64 object-cover rounded-lg mb-4"
            />
            <h2 className="text-2xl font-bold mb-2">{slide.title}</h2>
            <p className="mb-4">{slide.description}</p>
            <button className="px-6 py-2 bg-blue-500 text-white rounded-md">
              {slide.buttonText}
            </button>
          </div>
        ))}
      </div>

      {/* Navigation Arrows */}
      <button
        onClick={prevSlide}
        className="absolute top-1/2 left-4 -translate-y-1/2 bg-white p-2 rounded-full shadow-md hover:bg-gray-200"
      >
        &#8592;
      </button>
      <button
        onClick={nextSlide}
        className="absolute top-1/2 right-4 -translate-y-1/2 bg-white p-2 rounded-full shadow-md hover:bg-gray-200"
      >
        &#8594;
      </button>
    </div>
  );
}
